import 'dart:math';

var rand = new Random();

// Returns a random double greater than or equal to 0.0
// and less than 1.0
rand.nextDouble();

// Returns a random boolean value.
rand.nextBool();

// Returns a positive random integer greater or equal
// to 0 and less than 10
rand.nextInt(10);

